  return (a[field] || "").localeCompare(b[field] || "");
}

// 문자열 비교 (내림차순)
function compareStringDesc(a, b, field) {
  return (b[field] || "").localeCompare(a[field] || "");
}

// 숫자 비교 (오름차순)
function compareNumberAsc(a, b, field) {
  return (a[field] ?? 0) - (b[field] ?? 0);
}

// 숫자 비교 (내림차순)
function compareNumberDesc(a, b, field) {
  return (b[field] ?? 0) - (a[field] ?? 0);
}

// 날짜 비교 (오름차순: 오래된 → 최신)
function compareDateAsc(a, b, field) {
  return new Date(a[field]) - new Date(b[field]);
}

// 날짜 비교 (내림차순: 최신 → 오래된)
function compareDateDesc(a, b, field) {
  return new Date(b[field]) - new Date(a[field]);
}

// 정렬 모드에 따라 compare 함수를 달리 적용
function sortByMode(products, mode) {
  const sorted = [...products]; // 원본 배열을 복사한 뒤 정렬 (불변성 유지)
  sorted.sort((a, b) => {
    switch (mode) {
      case "createdDesc": // 최신순서 (createdAt 내림차순)
        return compareDateDesc(a, b, "createdAt");
      case "createdAsc": // 오래된순서 (createdAt 오름차순)
        return compareDateAsc(a, b, "createdAt");
      case "updatedDesc": // 업데이트순서 (updatedAt 내림차순)
        return compareDateDesc(a, b, "updatedAt");

      case "expirationDesc": // Expiration Date 높은순 (가장 나중에 만료되는 것이 앞으로)
        return compareDateDesc(a, b, "expirationDate");
      case "expirationAsc": // Expiration Date 낮은순
        return compareDateAsc(a, b, "expirationDate");

      case "quantityDesc": // Quantity 높은순
        return compareNumberDesc(a, b, "quantity");
      case "quantityAsc": // Quantity 낮은순
        return compareNumberAsc(a, b, "quantity");

      case "brandAsc": // Brand 알파벳순
        return compareStringAsc(a, b, "brand");
      case "brandDesc": // Brand 알파벳역순
        return compareStringDesc(a, b, "brand");

      case "categoryAsc":
        return compareStringAsc(a, b, "category");
      case "categoryDesc":
        return compareStringDesc(a, b, "category");

      case "productAsc":
        return compareStringAsc(a, b, "product");
      case "productDesc":
        return compareStringDesc(a, b, "product");

      case "locationAsc":
        return compareStringAsc(a, b, "location");
      case "locationDesc":
        return compareStringDesc(a, b, "location");

      default:
        // 정렬 옵션을 선택하지 않은 경우 (또는 "기본 정렬")
        return 0;
    }
  });
  return sorted;
}

const ProductsList = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // 검색 관련 state
  const [searchTerm, setSearchTerm] = useState("");
  const [tempSearchTerm, setTempSearchTerm] = useState("");
  const [selectedField, setSelectedField] = useState("All");

  // 정렬 관련 state
  const [sortMode, setSortMode] = useState(""); // 현재 선택된 정렬 모드

  // 페이지네이션을 위한 state
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10; // 페이지당 아이템 수

  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/products/");
      if (!response.ok) {
        throw new Error("Failed to fetch products");
      }
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      setError(error.message);
    }
  };

  // 삭제
  const handleDelete = async (id) => {
    if (!window.confirm("정말 삭제하시겠습니까?")) return;

    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5001/api/products/${id}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        throw new Error("Failed to delete product");
      }

      setProducts(products.filter((product) => product._id !== id));
    } catch (error) {
      alert("삭제 중 오류 발생: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  // 검색 필드
  const fields = [
    "All",
    "Brand",
    "Category",
    "Product",
    "Expiration Date",
    "Quantity",
    "Location",
    "CreatedAt",
    "UpdatedAt",
  ];

  // 특정 필드 값 추출 함수
  const getFieldValue = (product, field) => {
    switch (field) {
      case "Brand":
        return product.brand || "";
      case "Category":
        return product.category || "";
      case "Product":
        return product.product || "";
      case "Expiration Date":
        return product.expirationDate
          ? new Date(product.expirationDate).toLocaleDateString()
          : "";
      case "Quantity":
        return String(product.quantity ?? "");
      case "Location":
        return product.location || "";
      case "CreatedAt":
        return product.createdAt
          ? new Date(product.createdAt).toLocaleString()
          : "";
      case "UpdatedAt":
        return product.updatedAt
          ? new Date(product.updatedAt).toLocaleString()
          : "";
      default:
        return "";
    }
  };

  // 검색 버튼 클릭 시
  const handleSearch = () => {
    setSearchTerm(tempSearchTerm);
    setCurrentPage(1); // 검색할 때마다 페이지를 1로 초기화
  };

  // Enter 키로도 검색 가능하게
  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  // 필터링 로직
  const lowerSearchTerm = searchTerm.toLowerCase();

  const filteredProducts = products.filter((product) => {
    // 1) 검색할 필드가 "All"인 경우
    if (selectedField === "All") {
      const combinedString = [
        product.brand,
        product.category,
        product.product,
        product.expirationDate
          ? new Date(product.expirationDate).toLocaleDateString()
          : "",
        product.quantity,
        product.location,
        product.createdAt ? new Date(product.createdAt).toLocaleString() : "",
        product.updatedAt ? new Date(product.updatedAt).toLocaleString() : "",
      ]
        .join(" ")
        .toLowerCase();

      return combinedString.includes(lowerSearchTerm);
    } else {
      // 2) 특정 필드만 검색
      const fieldValue = getFieldValue(product, selectedField).toLowerCase();
      return fieldValue.includes(lowerSearchTerm);
    }
  });

  // 정렬 적용
  const sortedProducts = sortByMode(filteredProducts, sortMode);

  // 페이지네이션 로직
  const totalItems = sortedProducts.length;
  const totalPages = Math.ceil(totalItems / itemsPerPage);

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;

  const currentProducts = sortedProducts.slice(
    indexOfFirstItem,
    indexOfLastItem
  );

  // 검색/필터/정렬 리셋
  function handleSearchReset() {
    setSearchTerm("");
    setTempSearchTerm("");
    setSelectedField("All");
    setSortMode("");
    setCurrentPage(1);
  }

  // 페이지 이동
  const goToNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const goToPrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  return (
    <div className="productList-container">
      <h1 className="title">Products List</h1>

      {/* 검색 & 정렬 영역 */}
      <div className="controls-container">
        {/* 검색 영역 */}
        <div className="search-container">
          <select
            value={selectedField}
            onChange={(e) => setSelectedField(e.target.value)}
          >
            {fields.map((field) => (
              <option key={field} value={field}>
                {field}
              </option>
            ))}
          </select>
          <input
            type="text"
            placeholder="Please enter a search term..."
            value={tempSearchTerm}
            onChange={(e) => setTempSearchTerm(e.target.value)}
            onKeyDown={handleKeyDown}
          />
          <button onClick={handleSearch}>Search</button>
          <button onClick={handleSearchReset}>Reset</button>
        </div>

        {/* 정렬 영역 */}
        <div className="sort-container">
          <label htmlFor="sortMode">정렬 옵션:</label>
          <select
            id="sortMode"
            value={sortMode}
            onChange={(e) => {
              setSortMode(e.target.value);
              setCurrentPage(1); // 정렬 변경 시 페이지를 1로
            }}
          >
            <option value="">-- 정렬 없음 --</option>
            <option value="createdDesc">최신순서</option>
            <option value="updatedDesc">업데이트순서</option>
            <option value="expirationDesc">Expiration Date 높은순</option>
            <option value="expirationAsc">Expiration Date 낮은순</option>
            <option value="createdAsc">오래된순서</option>
            <option value="quantityDesc">Quantity 높은순</option>
            <option value="quantityAsc">Quantity 낮은순</option>
            <option value="brandAsc">Brand 알파벳순</option>
            <option value="brandDesc">Brand 알파벳역순</option>
            <option value="categoryAsc">Category 알파벳순</option>
            <option value="categoryDesc">Category 알파벳역순</option>
            <option value="productAsc">Product 알파벳순</option>
            <option value="productDesc">Product 알파벳역순</option>
            <option value="locationAsc">Location 알파벳순</option>
            <option value="locationDesc">Location 알파벳역순</option>
          </select>
        </div>
      </div>

      <table className="productList-table">
        {error && <p>{error}</p>}
        <thead>
          <tr className="productList-table-thead">
            <th>Brand</th>
            <th>Category</th>
            <th>Product</th>
            <th>Expiration Date</th>
            <th>Quantity</th>
            <th>Location</th>
            <th>CreatedAt</th>
            <th>UpdatedAt</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentProducts.length > 0 ? (
            currentProducts.map((product) => (
              <tr key={product._id}>
                <td>{product.brand}</td>
                <td>{product.category}</td>
                <td>{product.product}</td>
                <td>
                  {product.expirationDate
                    ? new Date(product.expirationDate).toLocaleDateString()
                    : ""}
                </td>
                <td>{product.quantity}</td>
                <td>{product.location}</td>
                <td>
                  {product.createdAt
                    ? new Date(product.createdAt).toLocaleString()
                    : ""}
                </td>
                <td>
                  {product.updatedAt
                    ? new Date(product.updatedAt).toLocaleString()
                    : ""}
                </td>
                <td>
                  <button
                    onClick={() => navigate(`/update-product/${product._id}`)}
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(product._id)}
                    disabled={loading}
                  >
                    {loading ? "Deleting..." : "Delete"}
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="9">No products available.</td>
            </tr>
          )}
        </tbody>
      </table>

      {/* 페이지네이션 버튼들 */}
      <div className="pagination-container">
        <button onClick={goToPrevPage} disabled={currentPage === 1}>
          Previous
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button onClick={goToNextPage} disabled={currentPage === totalPages}>
          Next
        </button>
      </div>

      <Link to="/AddProduct">AddProduct</Link>
    </div>
  );
};

export default ProductsList;
