import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./productsList.css";

const ProductsList = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // 실제 검색어
  const [searchTerm, setSearchTerm] = useState("");
  // 임시 검색어
  const [tempSearchTerm, setTempSearchTerm] = useState("");
  // 검색할 필드
  const [selectedField, setSelectedField] = useState("All");

  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/products/");
      if (!response.ok) {
        throw new Error("Failed to fetch products");
      }
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      setError(error.message);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("정말 삭제하시겠습니까?")) return;

    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5001/api/products/${id}`, {
        method: "DELETE",
      });
      if (!response.ok) {
        throw new Error("Failed to delete product");
      }

      setProducts(products.filter((product) => product._id !== id));
    } catch (error) {
      alert("삭제 중 오류 발생: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  const fields = [
    "All",
    "Brand",
    "Category",
    "Product",
    "Expiration Date",
    "Quantity",
    "Location",
    "CreatedAt",
    "UpdatedAt",
  ];

  // 필드별 값 추출
  const getFieldValue = (product, field) => {
    switch (field) {
      case "Brand":
        return product.brand || "";
      case "Category":
        return product.category || "";
      case "Product":
        return product.product || "";
      case "Expiration Date":
        return product.expirationDate
          ? new Date(product.expirationDate).toLocaleDateString()
          : "";
      case "Quantity":
        return String(product.quantity ?? "");
      case "Location":
        return product.location || "";
      case "CreatedAt":
        return product.createdAt
          ? new Date(product.createdAt).toLocaleString()
          : "";
      case "UpdatedAt":
        return product.updatedAt
          ? new Date(product.updatedAt).toLocaleString()
          : "";
      default:
        return "";
    }
  };

  // 검색 버튼 클릭 or 엔터 입력 시 실행
  const handleSearch = () => {
    setSearchTerm(tempSearchTerm);
  };
  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  // 필터링 로직
  const lowerSearchTerm = searchTerm.toLowerCase();
  const filteredProducts = products.filter((product) => {
    if (selectedField === "All") {
      // 모든 필드를 합쳐서 검색
      const combinedString = [
        product.brand,
        product.category,
        product.product,
        product.expirationDate
          ? new Date(product.expirationDate).toLocaleDateString()
          : "",
        product.quantity,
        product.location,
        product.createdAt ? new Date(product.createdAt).toLocaleString() : "",
        product.updatedAt ? new Date(product.updatedAt).toLocaleString() : "",
      ]
        .join(" ")
        .toLowerCase();

      return combinedString.includes(lowerSearchTerm);
    } else {
      // 특정 필드만 검색
      const fieldValue = getFieldValue(product, selectedField).toLowerCase();
      return fieldValue.includes(lowerSearchTerm);
    }
  });

  return (
    <div className="products-list__container">
      <h1 className="products-list__title">Products List</h1>

      {/* 에러 메시지 */}
      {error && <p className="products-list__error-message">{error}</p>}

      {/* 검색 영역 */}
      <div className="products-list__search-area">
        <input
          className="products-list__search-input"
          type="text"
          placeholder="검색어를 입력하세요..."
          value={tempSearchTerm}
          onChange={(e) => setTempSearchTerm(e.target.value)}
          onKeyDown={handleKeyDown}
        />

        <select
          className="products-list__search-select"
          value={selectedField}
          onChange={(e) => setSelectedField(e.target.value)}
        >
          {fields.map((field) => (
            <option key={field} value={field}>
              {field}
            </option>
          ))}
        </select>

        <button className="products-list__search-button" onClick={handleSearch}>
          검색
        </button>
      </div>

      {/* 테이블 */}
      <table className="products-list__table">
        <thead>
          <tr>
            <th>Brand</th>
            <th>Category</th>
            <th>Product</th>
            <th>Expiration Date</th>
            <th>Quantity</th>
            <th>Location</th>
            <th>CreatedAt</th>
            <th>UpdatedAt</th>
            <th>Actions</th>
          </tr>
        </thead>

        <tbody>
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <tr key={product._id}>
                <td>{product.brand}</td>
                <td>{product.category}</td>
                <td>{product.product}</td>
                <td>
                  {product.expirationDate
                    ? new Date(product.expirationDate).toLocaleDateString()
                    : ""}
                </td>
                <td>{product.quantity}</td>
                <td>{product.location}</td>
                <td>
                  {product.createdAt
                    ? new Date(product.createdAt).toLocaleString()
                    : ""}
                </td>
                <td>
                  {product.updatedAt
                    ? new Date(product.updatedAt).toLocaleString()
                    : ""}
                </td>
                <td>
                  <button
                    className="products-list__button products-list__button--update"
                    onClick={() => navigate(`/update-product/${product._id}`)}
                  >
                    Update
                  </button>
                  <button
                    className="products-list__button products-list__button--delete"
                    onClick={() => handleDelete(product._id)}
                    disabled={loading}
                  >
                    {loading ? "Deleting..." : "Delete"}
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="9">No products available.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ProductsList;
