import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const ProductsList = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  // ✅ API에서 제품 목록 가져오기
  const fetchProducts = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/products/");
      if (!response.ok) {
        throw new Error("Failed to fetch products");
      }
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      setError(error.message);
    }
  };

  // ✅ 제품 삭제 함수
  const handleDelete = async (id) => {
    if (!window.confirm("정말 삭제하시겠습니까?")) return;

    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5001/api/products/${id}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        throw new Error("Failed to delete product");
      }

      setProducts(products.filter((product) => product._id !== id));
    } catch (error) {
      alert("삭제 중 오류 발생: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: "1000px", margin: "auto", padding: "20px" }}>
      <h1>Products List</h1>
      {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}

      <table
        style={{ width: "100%", borderCollapse: "collapse", marginTop: "10px" }}
      >
        <thead>
          <tr style={{ backgroundColor: "#f2f2f2" }}>
            <th style={thStyle}>Id</th>
            <th style={thStyle}>Brand</th>
            <th style={thStyle}>Category</th>
            <th style={thStyle}>Product</th>
            <th style={thStyle}>Expiration Date</th>
            <th style={thStyle}>Quantity</th>
            <th style={thStyle}>Location</th>
            <th style={thStyle}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product._id} style={{ borderBottom: "1px solid #ddd" }}>
                <td style={tdStyle}>{product.id}</td>
                <td style={tdStyle}>{product.brand}</td>
                <td style={tdStyle}>{product.category}</td>
                <td style={tdStyle}>{product.product}</td>
                <td style={tdStyle}>
                  {new Date(product.expirationDate).toLocaleDateString()}
                </td>
                <td style={tdStyle}>{product.quantity}</td>
                <td style={tdStyle}>{product.location}</td>
                <td style={tdStyle}>
                  <button
                    onClick={() => navigate(`/update-product/${product._id}`)}
                    style={updateButtonStyle}
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(product._id)}
                    disabled={loading}
                    style={deleteButtonStyle}
                  >
                    {loading ? "Deleting..." : "Delete"}
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td
                colSpan="7"
                style={{
                  textAlign: "center",
                  padding: "10px",
                  fontStyle: "italic",
                }}
              >
                No products available.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

// 공통 스타일 정의
const thStyle = {
  padding: "10px",
  textAlign: "left",
  borderBottom: "2px solid #ccc",
};

const tdStyle = {
  padding: "8px",
  textAlign: "left",
  borderBottom: "1px solid #ddd",
};

const updateButtonStyle = {
  padding: "5px 10px",
  backgroundColor: "blue",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
  marginRight: "5px",
};

const deleteButtonStyle = {
  padding: "5px 10px",
  backgroundColor: "red",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
};

export default ProductsList;
