import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./productsList.css";

const ProductsList = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // 검색어 상태
  const [searchTerm, setSearchTerm] = useState("");

  // 어떤 컬럼(필드)을 검색할지 선택하는 상태 (초기값: "All")
  const [selectedField, setSelectedField] = useState("All");

  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/products/");
      if (!response.ok) {
        throw new Error("Failed to fetch products");
      }
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      setError(error.message);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("정말 삭제하시겠습니까?")) return;

    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5001/api/products/${id}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        throw new Error("Failed to delete product");
      }

      setProducts(products.filter((product) => product._id !== id));
    } catch (error) {
      alert("삭제 중 오류 발생: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  // ========================
  // 1) 검색 가능한 필드 목록
  // ========================
  const fields = [
    "All",
    "Brand",
    "Category",
    "Product",
    "Expiration Date",
    "Quantity",
    "Location",
    "CreatedAt",
    "UpdatedAt",
  ];

  // ========================
  // 2) 특정 필드의 문자열을 추출하는 함수
  // ========================
  const getFieldValue = (product, field) => {
    switch (field) {
      case "Brand":
        return product.brand || "";
      case "Category":
        return product.category || "";
      case "Product":
        return product.product || "";
      case "Expiration Date":
        // 날짜 필드는 문자열로 변환
        return product.expirationDate
          ? new Date(product.expirationDate).toLocaleDateString()
          : "";
      case "Quantity":
        // 숫자도 문자열로 변환
        return String(product.quantity ?? "");
      case "Location":
        return product.location || "";
      case "CreatedAt":
        return product.createdAt
          ? new Date(product.createdAt).toLocaleString()
          : "";
      case "UpdatedAt":
        return product.updatedAt
          ? new Date(product.updatedAt).toLocaleString()
          : "";
      default:
        return "";
    }
  };

  // ========================
  // 3) 필터 로직
  // ========================
  const lowerSearchTerm = searchTerm.toLowerCase();

  const filteredProducts = products.filter((product) => {
    // (1) "All"이면, 모든 필드 통합 검색
    if (selectedField === "All") {
      // 모든 필드를 합쳐 하나의 문자열로 만들고 소문자로 변환
      const combinedString = [
        product.brand,
        product.category,
        product.product,
        product.expirationDate
          ? new Date(product.expirationDate).toLocaleDateString()
          : "",
        product.quantity,
        product.location,
        product.createdAt ? new Date(product.createdAt).toLocaleString() : "",
        product.updatedAt ? new Date(product.updatedAt).toLocaleString() : "",
      ]
        .join(" ")
        .toLowerCase();
      // 통합 문자열에 검색어가 포함되어 있으면 true
      return combinedString.includes(lowerSearchTerm);
    } else {
      // (2) 특정 필드를 검색
      const fieldValue = getFieldValue(product, selectedField).toLowerCase();
      return fieldValue.includes(lowerSearchTerm);
    }
  });

  return (
    <div>
      <h1>Products List</h1>
      {error && <p>{error}</p>}

      {/* 검색 영역: 입력 + 드롭다운 */}
      <div style={{ marginBottom: "1rem", display: "flex", gap: "1rem" }}>
        {/* 검색어 입력 필드 */}
        <input
          type="text"
          placeholder="검색어를 입력하세요..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />

        {/* 검색할 필드 선택 드롭다운 */}
        <select
          value={selectedField}
          onChange={(e) => setSelectedField(e.target.value)}
        >
          {fields.map((field) => (
            <option key={field} value={field}>
              {field}
            </option>
          ))}
        </select>
      </div>

      <table>
        <thead>
          <tr>
            <th>Brand</th>
            <th>Category</th>
            <th>Product</th>
            <th>Expiration Date</th>
            <th>Quantity</th>
            <th>Location</th>
            <th>CreatedAt</th>
            <th>UpdatedAt</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <tr key={product._id}>
                <td>{product.brand}</td>
                <td>{product.category}</td>
                <td>{product.product}</td>
                <td>
                  {product.expirationDate
                    ? new Date(product.expirationDate).toLocaleDateString()
                    : ""}
                </td>
                <td>{product.quantity}</td>
                <td>{product.location}</td>
                <td>
                  {product.createdAt
                    ? new Date(product.createdAt).toLocaleString()
                    : ""}
                </td>
                <td>
                  {product.updatedAt
                    ? new Date(product.updatedAt).toLocaleString()
                    : ""}
                </td>
                <td>
                  <button
                    onClick={() => navigate(`/update-product/${product._id}`)}
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(product._id)}
                    disabled={loading}
                  >
                    {loading ? "Deleting..." : "Delete"}
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="9">No products available.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ProductsList;
