import React, { useState } from "react";
import "./addProduct.css"; // 이 파일에서 클래스에 대한 스타일을 작성

const AddProduct = () => {
  const [brand, setBrand] = useState("");
  const [category, setCategory] = useState("");
  const [product, setProduct] = useState("");
  const [expirationDate, setExpirationDate] = useState("");
  const [quantity, setQuantity] = useState("");
  const [location, setLocation] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (
      !brand ||
      !category ||
      !product ||
      !expirationDate ||
      !quantity ||
      !location
    ) {
      setMessage("모든 필드를 채워주세요.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5001/api/products/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          brand,
          category,
          product,
          expirationDate,
          quantity: parseInt(quantity, 10),
          location,
        }),
      });

      if (response.ok) {
        const result = await response.json();
        setMessage(`Product added successfully! ID: ${result._id}`);
        reset();
      } else {
        const error = await response.json();
        setMessage(`Error: ${error.message}`);
      }
    } catch (err) {
      setMessage(`Error: ${err.message}`);
    }
  };

  function reset() {
    setBrand("");
    setCategory("");
    setProduct("");
    setExpirationDate("");
    setQuantity("");
    setLocation("");
  }

  return (
    <div className="add-product__container">
      <h2 className="add-product__title">Add Products</h2>

      <form className="add-product__form" onSubmit={handleSubmit}>
        {/* Brand */}
        <div className="form-group">
          <label className="form-group__label" htmlFor="brand">
            Brand:
          </label>
          <input
            className="form-group__input"
            type="text"
            id="brand"
            value={brand}
            onChange={(e) => setBrand(e.target.value)}
            required
          />
        </div>

        {/* Category */}
        <div className="form-group">
          <label className="form-group__label" htmlFor="category">
            Category:
          </label>
          <input
            className="form-group__input"
            type="text"
            id="category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
          />
        </div>

        {/* Product */}
        <div className="form-group">
          <label className="form-group__label" htmlFor="product">
            Product:
          </label>
          <input
            className="form-group__input"
            type="text"
            id="product"
            value={product}
            onChange={(e) => setProduct(e.target.value)}
            required
          />
        </div>

        {/* Expiration Date */}
        <div className="form-group">
          <label className="form-group__label" htmlFor="expirationDate">
            Expiration Date:
          </label>
          <input
            className="form-group__input"
            type="date"
            id="expirationDate"
            value={expirationDate}
            onChange={(e) => setExpirationDate(e.target.value)}
            required
          />
        </div>

        {/* Quantity */}
        <div className="form-group">
          <label className="form-group__label" htmlFor="quantity">
            Quantity:
          </label>
          <input
            className="form-group__input"
            type="number"
            id="quantity"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
            required
          />
        </div>

        {/* Location */}
        <div className="form-group">
          <label className="form-group__label" htmlFor="location">
            Location:
          </label>
          <input
            className="form-group__input"
            type="text"
            id="location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          />
        </div>

        {/* Buttons */}
        <div className="form-actions">
          <button className="form-actions__submit" type="submit">
            Add Product
          </button>
          <button className="form-actions__reset" type="button" onClick={reset}>
            Reset
          </button>
        </div>
        <button></button>
      </form>

      {message && <div className="add-product__message">{message}</div>}
    </div>
  );
};

export default AddProduct;
