import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

const UpdateProduct = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 날짜/시간 포맷 함수 (예: 2025-01-31 13:45:59)
  const formatDateTime = (dateString) => {
    if (!dateString) return "";
    const date = new Date(dateString);
    return date.toLocaleString("ko-KR", {
      year: "numeric",
      month: "2-digit",
      day: "2-digit",
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit",
    });
  };

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(
          `http://localhost:5001/api/products/${id}`
        );
        if (!response.ok) throw new Error("Failed to fetch product");

        const data = await response.json();
        setProduct(data);
      } catch (error) {
        setError(error.message);
        console.error("Error fetching product:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleChange = (e) => {
    setProduct({
      ...product,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5001/api/products/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(product),
      });

      if (!response.ok) throw new Error("Failed to update product");

      alert("Product updated successfully!");
      navigate("/");
    } catch (error) {
      alert("Error updating product: " + error.message);
    }
  };

  if (loading) return <p>Loading product data...</p>;
  if (error) return <p>{error}</p>;
  if (!product) return <p>Error loading product data.</p>;

  return (
    <div>
      <h2>Update Product</h2>
      <form onSubmit={handleSubmit}>
        {Object.keys(product).map((key) => {
          // _id 필드는 수정하지 않으므로 표시 X
          if (key === "_id") return null;

          return (
            <div key={key} style={{ marginBottom: "0.5rem" }}>
              <label htmlFor={key} style={{ marginRight: "0.5rem" }}>
                {key.charAt(0).toUpperCase() + key.slice(1)}:
              </label>

              {/* createdAt, updatedAt은 보기 편하게 날짜/시간 형식으로 표시 (readOnly) */}
              {key === "createdAt" || key === "updatedAt" ? (
                <input
                  type="text"
                  id={key}
                  name={key}
                  value={formatDateTime(product[key])}
                  readOnly
                />
              ) : (
                <input
                  type={
                    key === "quantity"
                      ? "number"
                      : key === "expirationDate"
                      ? "date"
                      : "text"
                  }
                  id={key}
                  name={key}
                  value={
                    key === "expirationDate"
                      ? product.expirationDate
                        ? new Date(product.expirationDate)
                            .toISOString()
                            .slice(0, 10) // "YYYY-MM-DD" 형태로
                        : ""
                      : product[key] || ""
                  }
                  onChange={handleChange}
                  required
                />
              )}
            </div>
          );
        })}
        <button type="submit">Update Product</button>
      </form>
    </div>
  );
};

export default UpdateProduct;
