import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./productsList.css";

const ProductsList = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  // 카테고리 선택 상태
  const [selectedCategory, setSelectedCategory] = useState("All");

  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/products/");
      if (!response.ok) {
        throw new Error("Failed to fetch products");
      }
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      setError(error.message);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("정말 삭제하시겠습니까?")) return;

    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5001/api/products/${id}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        throw new Error("Failed to delete product");
      }

      setProducts(products.filter((product) => product._id !== id));
    } catch (error) {
      alert("삭제 중 오류 발생: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  // ================================
  // 1. 전체 제품 목록에서 카테고리 리스트 뽑아내기
  // ================================
  // 중복 제거 후 'All' 추가
  const categories = ["All", ...new Set(products.map((p) => p.category))];

  // ================================
  // 2. 검색어 + 카테고리 필터 적용
  // ================================
  const filteredProducts = products.filter((product) => {
    // 검색어 소문자 변환
    const lowerSearchTerm = searchTerm.toLowerCase();

    // 검색어 조건: brand / category / product / location 중 하나에 포함
    const matchesSearchTerm =
      product.brand.toLowerCase().includes(lowerSearchTerm) ||
      product.category.toLowerCase().includes(lowerSearchTerm) ||
      product.product.toLowerCase().includes(lowerSearchTerm) ||
      product.location.toLowerCase().includes(lowerSearchTerm);

    // 카테고리 조건: 드롭다운에서 "All"이거나, 실제 카테고리가 선택된 값과 동일
    const matchesCategory =
      selectedCategory === "All" || product.category === selectedCategory;

    // 두 조건이 모두 참이면 필터 통과
    return matchesSearchTerm && matchesCategory;
  });

  return (
    <div>
      <h1>Products List</h1>
      {error && <p>{error}</p>}

      {/* 검색 영역 */}
      <div style={{ marginBottom: "1rem", display: "flex", gap: "1rem" }}>
        {/* 검색어 입력 필드 */}
        <input
          type="text"
          placeholder="검색어를 입력하세요..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        {/* 카테고리 선택 드롭다운 */}
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          {categories.map((cat) => (
            <option key={cat} value={cat}>
              {cat}
            </option>
          ))}
        </select>
      </div>

      {/* 제품 목록 테이블 */}
      <table>
        <thead>
          <tr>
            <th>Brand</th>
            <th>Category</th>
            <th>Product</th>
            <th>Expiration Date</th>
            <th>Quantity</th>
            <th>Location</th>
            <th>CreatedAt</th>
            <th>UpdatedAt</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <tr key={product._id}>
                <td>{product.brand}</td>
                <td>{product.category}</td>
                <td>{product.product}</td>
                <td>{new Date(product.expirationDate).toLocaleDateString()}</td>
                <td>{product.quantity}</td>
                <td>{product.location}</td>
                <td>{product.createdAt}</td>
                <td>{product.updatedAt}</td>
                <td>
                  <button
                    onClick={() => navigate(`/update-product/${product._id}`)}
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(product._id)}
                    disabled={loading}
                  >
                    {loading ? "Deleting..." : "Delete"}
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="9">No products available.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ProductsList;
