import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./productsList.css";
import { Link } from "react-router-dom";

const ProductsList = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [tempSearchTerm, setTempSearchTerm] = useState("");
  const [selectedField, setSelectedField] = useState("All");

  // 페이지네이션을 위한 state
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10; // 페이지당 아이템 수

  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/products/");
      if (!response.ok) {
        throw new Error("Failed to fetch products");
      }
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      setError(error.message);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("정말 삭제하시겠습니까?")) return;

    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5001/api/products/${id}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        throw new Error("Failed to delete product");
      }

      setProducts(products.filter((product) => product._id !== id));
    } catch (error) {
      alert("삭제 중 오류 발생: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  const fields = [
    "All",
    "Brand",
    "Category",
    "Product",
    "Expiration Date",
    "Quantity",
    "Location",
    "CreatedAt",
    "UpdatedAt",
  ];

  const getFieldValue = (product, field) => {
    switch (field) {
      case "Brand":
        return product.brand || "";
      case "Category":
        return product.category || "";
      case "Product":
        return product.product || "";
      case "Expiration Date":
        return product.expirationDate
          ? new Date(product.expirationDate).toLocaleDateString()
          : "";
      case "Quantity":
        return String(product.quantity ?? "");
      case "Location":
        return product.location || "";
      case "CreatedAt":
        return product.createdAt
          ? new Date(product.createdAt).toLocaleString()
          : "";
      case "UpdatedAt":
        return product.updatedAt
          ? new Date(product.updatedAt).toLocaleString()
          : "";
      default:
        return "";
    }
  };

  // 검색 버튼 클릭 시
  const handleSearch = () => {
    setSearchTerm(tempSearchTerm);
    setCurrentPage(1); // 검색할 때마다 페이지를 1로 초기화
  };

  // Enter 키로도 검색 가능하게
  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  // 필터링 로직
  const lowerSearchTerm = searchTerm.toLowerCase();

  const filteredProducts = products.filter((product) => {
    // 1) 검색할 필드가 "All"인 경우
    if (selectedField === "All") {
      const combinedString = [
        product.brand,
        product.category,
        product.product,
        product.expirationDate
          ? new Date(product.expirationDate).toLocaleDateString()
          : "",
        product.quantity,
        product.location,
        product.createdAt ? new Date(product.createdAt).toLocaleString() : "",
        product.updatedAt ? new Date(product.updatedAt).toLocaleString() : "",
      ]
        .join(" ")
        .toLowerCase();

      return combinedString.includes(lowerSearchTerm);
    }
    // 2) 특정 필드만 검색
    else {
      const fieldValue = getFieldValue(product, selectedField).toLowerCase();
      return fieldValue.includes(lowerSearchTerm);
    }
  });

  // 페이지네이션을 위해 현재 페이지에 표시할 아이템만 추출
  const totalItems = filteredProducts.length; // 전체 아이템 수
  const totalPages = Math.ceil(totalItems / itemsPerPage); // 총 페이지 수

  // 현재 페이지의 첫 아이템 인덱스와 마지막 아이템 인덱스
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;

  // 현재 페이지에 보여줄 제품 목록
  const currentProducts = filteredProducts.slice(
    indexOfFirstItem,
    indexOfLastItem
  );

  // 페이지를 리셋하는 함수
  function handleSearchReset() {
    setSearchTerm("");
    setTempSearchTerm("");
    setSelectedField("All");
    setCurrentPage(1);
  }

  // 페이지 이동 함수
  const goToNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const goToPrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  return (
    <div className="productList-container">
      <h1 className="title">Products List</h1>
      <div className="search-container">
        <select
          value={selectedField}
          onChange={(e) => setSelectedField(e.target.value)}
        >
          {fields.map((field) => (
            <option key={field} value={field}>
              {field}
            </option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Please enter a search term..."
          value={tempSearchTerm}
          onChange={(e) => setTempSearchTerm(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <button onClick={handleSearch}>Search</button>
        <button onClick={handleSearchReset}>Reset</button>
      </div>
      <table className="productList-table">
        {error && <p>{error}</p>}
        <thead>
          <tr className="productList-table-thead">
            <th>Brand</th>
            <th>Category</th>
            <th>Product</th>
            <th>Expiration Date</th>
            <th>Quantity</th>
            <th>Location</th>
            <th>CreatedAt</th>
            <th>UpdatedAt</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentProducts.length > 0 ? (
            currentProducts.map((product) => (
              <tr key={product._id}>
                <td>{product.brand}</td>
                <td>{product.category}</td>
                <td>{product.product}</td>
                <td>
                  {product.expirationDate
                    ? new Date(product.expirationDate).toLocaleDateString()
                    : ""}
                </td>
                <td>{product.quantity}</td>
                <td>{product.location}</td>
                <td>
                  {product.createdAt
                    ? new Date(product.createdAt).toLocaleString()
                    : ""}
                </td>
                <td>
                  {product.updatedAt
                    ? new Date(product.updatedAt).toLocaleString()
                    : ""}
                </td>
                <td>
                  <button
                    onClick={() => navigate(`/update-product/${product._id}`)}
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(product._id)}
                    disabled={loading}
                  >
                    {loading ? "Deleting..." : "Delete"}
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="9">No products available.</td>
            </tr>
          )}
        </tbody>
      </table>
      {/* 페이지네이션 버튼들 */}
      <div className="pagination-container">
        <button onClick={goToPrevPage} disabled={currentPage === 1}>
          Previous
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button onClick={goToNextPage} disabled={currentPage === totalPages}>
          Next
        </button>
      </div>
      <Link to="/AddProduct">AddProduct</Link>
    </div>
  );
};

export default ProductsList;
